if ( 'serviceWorker' in navigator ) 
{
	navigator.serviceWorker
	.register( 'sw.js' )
}

function set_tip_percentage( percentage )
{
	tip.value = percentage;

	update_bill_account();
}

function update_bill_account()
{
	resume_bill_account.innerHTML = numeral( bill_account.value ).format( '0,0.00' );

	resume_tip.innerHTML = numeral( bill_account.value * ( tip.value / 100 ) ).format( '0,0.00' );

	resume_total.innerHTML = numeral( bill_account.value * ( ( tip.value / 100 ) + 1 ) ).format( '0,0.00' );

	resume_per_person.innerHTML = numeral( ( bill_account.value * ( ( tip.value / 100 ) + 1 ) ) / people.value ).format( '0,0.00' );
}

function person( sign )
{
	if ( people.value > 1 )
	{
		if ( sign == 'plus' )
		{
			people.value = parseInt( people.value ) + 1;

		}else
		{
			people.value = parseInt( people.value ) - 1;

		}
	}else
	{
		if ( sign == 'plus' )
		{
			people.value = parseInt( people.value ) + 1;

		}

	}

	update_bill_account();
}

function set_lang( lang )
{
	let array_lang;

	if ( lang == 'en' ) // Español
	{
		array_lang = lang_en;

	}else if ( lang == 'es' ) // Inglés
	{
		array_lang = lang_es;

	}else if ( lang == 'fr' ) // Francés
	{
		array_lang = lang_fr;

	}else if ( lang == 'pr' ) // Portugués
	{
		array_lang = lang_pr;

	}else // Inglés por default
	{
		array_lang = lang_en;

	}

	for ( let i = 0; i < array_lang.length; i++ )
	{
		document.getElementById( array_lang[i].id ).innerHTML = array_lang[i].text;
	}
}
